import './App.css';
import {useEffect, useState} from 'react';

const tasksArray = [
  {name:"Angular",category:"progress", bgcolor: "yellow"},
  {name:"React", category:"progress", bgcolor:"pink"},
  {name:"Vue", category:"complete", bgcolor:"skyblue"}
];

function App() {
  const [task, setTask] = useState(tasksArray);

  onDragStart = (ev, id) => {
    console.log('dragstart:',id);
    ev.dataTransfer.setData("id", id);
    }

    onDragOver = (ev) => {
        ev.preventDefault();
    }

    onDrop = (ev, cat) => {
      let id = ev.dataTransfer.getData("id");

      let tasks = this.state.tasks.filter((task) => {
          if (task.name == id) {
              task.category = cat;
          }
          return task;
      });

      this.setState({
          ...this.state,
          tasks
      });
    }

  return (
    <div className="app">
        <div className="progress">
              first
              {
              task.map ((t) =>
                  <div key={t.name}
                      onDragStart = {(e) => onDragStart(e, t.name)}
                      draggable
                      className="draggable"
                      style = {{backgroundColor: t.bgcolor}}>
                      {t.name}
                  </div>
              )     
             }
        </div>

        <div className="complete"> 
              second
        </div>
    </div>
  );
}

export default App;
