function Api() {

  const api = 'http://apimeme.com/meme?meme=$1&top=$2&bottom=$3';
  const imageArray= ['Advice-Doge', 'Angry-Birds-Pig', 'Baby-Godfather', 'Close-Enough'];
  return (<>
    <h4>API Call</h4>
    <div className="section">

      <fieldset style={{width: '50%'}}>
        <legend>Create</legend>
        <form style={{paddingTop: '2%', textAlign: 'left'}}>
          <label htmlFor="toptxt">Top </label><br></br>
          <input type="text" id="toptxt" placeholder="Top text"></input>
          <br></br>
          <label htmlFor="bottomtxt">Bottom </label>
          <br></br>
          <input type="text" id="bottomtxt" placeholder="Bottom text"></input>
        </form>
      </fieldset>


      <fieldset style={{width: '50%'}} className='flexwrap'>
        <legend>Choose</legend>
        { imageArray.map((i,j) => {
          return(<div key={j} style={{margin:'4%', borderStyle: 'dotted'}}>
            <img id={i} src={`http://apimeme.com/thumbnail?name=${i}`}></img>
        </div>);
        })}
      </fieldset>
    </div>
    <button className='buttons'>Generate</button>
  </>);
}

export default Api;
