import DragDrop from './DragDrop';
import SideBar from './SideBar';
import Forms from './Forms';
import {useState} from 'react';

const completedArray = [
  {name:"one", category:"complete", bgcolor:"skyblue"}
];

const pendingArray = [
  {name:"Two",category:"progress", bgcolor: "yellow"},
  {name:"Three", category:"progress", bgcolor:"pink"}
];

const completedAlphaArray = [
  {name:"A", category:"complete", bgcolor:"skyblue"}
];

const pendingAlphaArray = [
  {name:"B",category:"progress", bgcolor: "yellow"},
  {name:"C", category:"progress", bgcolor:"pink"},
  {name:"D",category:"progress", bgcolor: "red"}
];

function Container() {
  const [showDiv, setShowDiv] = useState('dragdrop');

  const hideShow = (id) => {
    setShowDiv(id);
  }

  return (
    <div className="app">
      <SideBar hideShow={hideShow}/>
      <div className="container">
        {showDiv == 'dragdrop' ?
        <div id='dragdrop'> 
           <h4>Drag Drop</h4>
          <DragDrop completedArray={completedArray} pendingArray={pendingArray} />
          <hr></hr>
          <DragDrop completedArray={completedAlphaArray} pendingArray={pendingAlphaArray} />
        </div> : null}

        {showDiv == 'forms' ?
        <div id='forms'> Forms
            <Forms/>
        </div> : null}
      </div>      
    </div>
  );
}

export default Container;
