import './App.css';
import {useEffect, useState} from 'react';

const completedArray = [
  {name:"A", category:"complete", bgcolor:"skyblue"}
];

const pendingArray = [
  {name:"B",category:"progress", bgcolor: "yellow"},
  {name:"C", category:"progress", bgcolor:"pink"},

  {name:"D",category:"progress", bgcolor: "red"},
  {name:"E", category:"progress", bgcolor:"green"},

  {name:"F",category:"progress", bgcolor: "violet"},
  {name:"G", category:"progress", bgcolor:"purple"},

  {name:"H",category:"progress", bgcolor: "grey"},
  {name:"I", category:"progress", bgcolor:"brown"},

  {name:"J",category:"progress", bgcolor: "skyblue"},
  {name:"K", category:"progress", bgcolor:"darkgreen"}
];

function AppAlphabets() {
  const [completed, setCompletedTask] = useState(completedArray);
  const [progress, setProgressTask] = useState(pendingArray);

  const onDragStart = (ev, id) => {
    console.log('dragstart:',id);
    ev.dataTransfer.setData("id", id);
    }

    const onDragOver = (ev) => {
        ev.preventDefault();
    }

    const onDrop = (ev, cat) => {
      let id = ev.dataTransfer.getData("id");

      let tasks = progress.filter((task) => {
          if (task.name == id) {
              task.category = cat;
              return task;
          }
      });
      setCompletedTask([...completed,...tasks]);

    }

  return (
    <div>
        <div className="progress" onDragOver={(e)=>onDragOver(e)}
                    onDrop={(e)=>{onDrop(e, "progress")}}>
              third
              {
              progress.map ((t) =>
                  <div key={t.name}
                      onDragStart = {(e) => onDragStart(e, t.name)}
                      draggable
                      className="draggable"
                      style = {{backgroundColor: t.bgcolor}}>
                      {t.name}
                  </div>
              )     
             }
        </div>

        <div className="complete" 
                    onDragOver={(e)=>onDragOver(e)}
                    onDrop={(e)=>onDrop(e, "complete")}> 
              fourth
              {
              completed.map ((t) => { debugger;
                  return (<div key={t.name}
                      onDragStart = {(e) => onDragStart(e, t.name)}
                      draggable
                      className="draggable"
                      style = {{backgroundColor: t.bgcolor}}>
                      {t.name}
                  </div>);
              }
              )     
             }
        </div>
    </div>
  );
}

export default AppAlphabets;
