import {useState, useEffect} from 'react';
import axios from 'axios';
import userEvent from '@testing-library/user-event';

const api = `http://apimeme.com/meme?meme=1&top=2&bottom=3`,
imageArray= ['Advice-Doge', 'Angry-Birds-Pig', 'Baby-Godfather', 'Close-Enough'],
apiUrl = {tt:'',bt:'',id:'',img:''};

function Api() {
const [request, setRequest] = useState(apiUrl),
[error, setError] = useState(false),
[images, setImages] = useState(null);


useEffect(()=> {
  axios.get('https://api.memegen.link/images')
  .then(response => {
    setImages(response.data.slice(2,8));
  })
},[])

const click = (e) => {
  setRequest({...request, id: e.target.id, img: e.target.id.split('/')[4]});
}

const blur = (e) => {
  let id = e.target.id, value = e.target.value, tt = request.tt, bt = request.bt;
  if(id == 'toptxt') {
    tt = value;
  }else{
    bt = value;
  }
  setRequest({...request,tt: tt, bt:bt});
}

const clickGenerate = (e) => {
  let reqArray = Object.values(request); debugger;
  if(reqArray[0] && reqArray[1] && reqArray[2]){
    setError(false);
    axios.get(`https://api.memegen.link/images/${reqArray[2]}/${reqArray[0]}/${reqArray[1]}.jpg`)
    .then(
    response => {
          debugger;
    })
    .catch(
      error => {
        debugger;

      }
    )
  }else{
    setError(true);
  }
}

return (<>
    <h4>API Call</h4>
    <div style={{color: error ? 'red' : 'blue'}}>Enter all three fields!</div>
    <div className="section">
      <fieldset style={{width: '50%'}}>
        <legend>Create</legend>
        <form style={{paddingTop: '2%', textAlign: 'left'}}>
          <label htmlFor="toptxt">Top </label><br></br>
          <input type="text" id="toptxt" placeholder="Top text" onBlur={blur}></input>
          <br></br>
          <label htmlFor="bottomtxt">Bottom </label>
          <br></br>
          <input type="text" id="bottomtxt" placeholder="Bottom text" onBlur={blur}></input>
        </form>
      </fieldset>


      <fieldset style={{width: '50%'}} className='flexwrap' >
        <legend>Choose</legend>
        <div className='flexwrap' onClick={click}>
        {images ? images.map((i,j) => {
          const color = request.id == i.url ? 'blue' : 'black'; 
          return(<img className='imageFrame' style={{borderColor: color}} id={i.url} src={i.url}></img>);
        }) : null}
        </div>
      </fieldset>
    </div>
    <button className='buttons' onClick={clickGenerate} style={{width: '40%', marginTop: '2%', padding:'5px'}}>Generate</button>
  </>);
}

export default Api;
