import './App.css';
import {useEffect, useState} from 'react';

const tasksArray = [
  {name:"Angular",category:"progress", bgcolor: "yellow"},
  {name:"React", category:"progress", bgcolor:"pink"},
  {name:"Vue", category:"complete", bgcolor:"skyblue"}
];

function App() {
  const [task, setTask] = useState(tasksArray);


  return (
    <div className="app">
        <div className="progress">
              first
              {
              task.forEach ((t) => {
                  <div key={t.name} 
                      onDragStart = {(e) => this.onDragStart(e, t.name)}
                      draggable
                      className="draggable"
                      style = {{backgroundColor: t.bgcolor}}>
                      {t.name}
                  </div>
              })       
             }
        </div>

        <div className="complete"> 
              second
        </div>
    </div>
  );
}

export default App;
