import React from "react";
import axios from "axios";

const WrapperComponent = (Component, type) => {
  return class extends React.Component {
    state = {
      data: {
        personal: null,
        address: null,
        employment: null,
      },
    };

    componentDidMount() {
      this.getData();
    }

    getData = () => {
      axios
        .get("http://jsonplaceholder.typicode.com/users")
        .then((response) => {
          const data = response.data[0],
            { name, email, username, website } = data,
            personal = {
              name: name,
              email: email,
              username: username,
              website: website,
            };

          const { street, zipcode, city } = data.address;
          this.setState({
            personal: personal,
            address: { street: street, zipcode: zipcode, city: city },
          });
        })
        .catch();
    };

    render() {
      return (
        <Component>
          {type === "personal" ? (
            <div>
              {this.state.personal?.name}
              <br></br>
              <br></br>
              {this.state.personal?.email}
              <br></br>
              <br></br>
              {this.state.personal?.website}
              <br></br>
              <br></br>
              {this.state.personal?.username}
            </div>
          ) : type === "address" ? (
            <div>{this.state?.address?.city}</div>
          ) : null}
        </Component>
      );
    }
  };
};

export default WrapperComponent;
