import React from "react";
class Counter extends React.Component {
  state = {
    count: 0,
  };

  click = (e) => {
    if (e.target.id === "increment") {
      setCount(count + 1);
    } else {
      setCount(count - 1);
    }
  };

  render() {
    if (this.props.error && count === 5) {
      throw new Error("error");
    }

    return (
      <div style={{ width: "50%" }}>
        <fieldset>
          <legend>{props.name}</legend>
          <div>{count}</div>
          <button id="increment" onClick={click}>
            Increment
          </button>
          <button id="decrement" onClick={click}>
            Decrement
          </button>
        </fieldset>
      </div>
    );
  }
}

export default Counter;
