import { useSelector, useDispatch } from "react-redux";
import { useState } from "react";
import Modal from "../components/Modal";

function PostList() {
  const list = useSelector((state) => state),
    dispatch = useDispatch(),
    [showModal, setShowModal] = useState(false);

  const click = (e) => {
    if (e.target.name == "delete") {
      dispatch({
        type: "DELETE_POST",
        id: e.target.id,
      });
    } else {
    }
  };

  const handleSubmit = (e) => {
    modalClose();
  };

  const modalOpen = (e) => {
    setShowModal(true);
  };

  const modalClose = () => {
    setShowModal(false);
  };

  return (
    <>
      <Modal show={showModal} handleClose={(e) => modalClose(e)}>
        <h2>Hello Modal</h2>
        <div className="form-group">
          <label>Enter Name:</label>
          <input
            type="text"
            name="modalInputName"
            onChange={(e) => handleChange(e)}
            className="form-control"
          />
        </div>
        <div className="form-group">
          <button onClick={(e) => handleSubmit(e)} type="button">
            Save
          </button>
        </div>
      </Modal>
      <div className="section">
        <fieldset style={{ width: "100%" }}>
          <legend>Post List</legend>
          <table className="table" onClick={click}>
            <thead>
              <tr>
                <th>Title</th>
                <th>Message</th>
              </tr>
            </thead>
            {list.map((post, id) => {
              return (
                <tr key={post.id}>
                  <td>{post.title}</td>
                  <td>{post.message}</td>
                  <td>
                    <button id={post.id} name="edit" className="buttons">
                      edit
                    </button>
                  </td>
                  <td>
                    <button id={post.id} name="delete" className="buttons">
                      delete
                    </button>
                  </td>
                </tr>
              );
            })}
          </table>
        </fieldset>
        {/* {showModal ? <Modal></Modal> : null} */}
      </div>
    </>
  );
}

export default PostList;
