import {useEffect, useState} from 'react';

// const completedArray = [
//   {name:"One", category:"complete", bgcolor:"skyblue"}
// ];

// const pendingArray = [
//   {name:"Two",category:"progress", bgcolor: "yellow"},
//   {name:"Three", category:"progress", bgcolor:"pink"}
// ];

function Numbers(props) {
  const [completed, setCompletedTask] = useState(props.completedArray);
  const [progress, setProgressTask] = useState(props.pendingArray);

  const onDragStart = (ev, id) => {
    console.log('dragstart:',id);
    ev.dataTransfer.setData("id", id);
    }

    const onDragOver = (ev) => {
        ev.preventDefault();
    }

    const onDrop = (ev, cat) => {
      let id = ev.dataTransfer.getData("id");

      let tasks = progress.filter((task) => {
          if (task.name == id) {
              task.category = cat;
              return task;
          }
      });
      setCompletedTask([...completed,...tasks]);

    }

  return (
    <div className="section">
        <div className="progress" onDragOver={(e)=>onDragOver(e)}
                    onDrop={(e)=>{onDrop(e, "progress")}}>
              first
              {
              progress.map ((t) =>
                  <div key={t.name}
                      onDragStart = {(e) => onDragStart(e, t.name)}
                      draggable
                      className="draggable"
                      style = {{backgroundColor: t.bgcolor}}>
                      {t.name}
                  </div>
              )     
             }
        </div>

        <div className="complete" 
                    onDragOver={(e)=>onDragOver(e)}
                    onDrop={(e)=>onDrop(e, "complete")}> 
              second
              {
              completed.map ((t) => {
                  return (<div key={t.name}
                      onDragStart = {(e) => onDragStart(e, t.name)}
                      draggable
                      className="draggable"
                      style = {{backgroundColor: t.bgcolor}}>
                      {t.name}
                  </div>);
              }
              )     
             }
        </div>
    </div>
  );
}

export default Numbers;
