import './App.css';
import {useEffect, useState} from 'react';

const tasksArray = [
  {name:"Angular",category:"progress", bgcolor: "yellow"},
  {name:"React", category:"progress", bgcolor:"pink"},
  {name:"Vue", category:"complete", bgcolor:"skyblue"}
];

function App() {
  const [task, setTask] = useState(tasksArray);

  useEffect(()=>{
    var tasks = {
      progress: [],
      complete: []
    }

    task.forEach ((t) => {
      tasks[t.category].push(
          <div key={t.name} 
              onDragStart = {(e) => this.onDragStart(e, t.name)}
              draggable
              className="draggable"
              style = {{backgroundColor: t.bgcolor}}>
              {t.name}
          </div>
      );
  });

  },[]);

  return (
    <div className="app">
        <div className="progress">
              first
        </div>

        <div className="droppable"> 
              second
        </div>
    </div>
  );
}

export default App;
