import './App.css';
import {useEffect, useState} from 'react';

const alpha = Array.from(Array(26)).map((e, i) => i + 65);
const alphabet = alpha.map((x) => String.fromCharCode(x));

function AppAlphabets() {
  const [completed, setCompletedTask] = useState();
  const [progress, setProgressTask] = useState(alphabet);

  const onDragStart = (ev, id) => {
    console.log('dragstart:',id);
    ev.dataTransfer.setData("id", id);
    }

    const onDragOver = (ev) => {
        ev.preventDefault();
    }

    const onDrop = (ev, cat) => {
      let id = ev.dataTransfer.getData("id");

      let tasks = progress.filter((task) => {
          if (task.name == id) {
              task.category = cat;
              return task;
          }
      });
      setCompletedTask([...completed,...tasks]);

    }

  return (
    <div className="app">
        <div className="progress" onDragOver={(e)=>onDragOver(e)}
                    onDrop={(e)=>{onDrop(e, "progress")}}>
              first
              {
              progress.map ((t) =>
                  <div key={t}
                      onDragStart = {(e) => onDragStart(e, t)}
                      draggable
                      className="draggable">
                      {t.name}
                  </div>
              )     
             }
        </div>

        <div className="complete" 
                    onDragOver={(e)=>onDragOver(e)}
                    onDrop={(e)=>onDrop(e, "complete")}> 
              second
              {/* {
              completed.map ((t) => { debugger;
                  return (<div key={t.name}
                      onDragStart = {(e) => onDragStart(e, t.name)}
                      draggable
                      className="draggable"
                      style = {{backgroundColor: t.bgcolor}}>
                      {t.name}
                  </div>);
              }
              )     
             } */}
        </div>
    </div>
  );
}

export default AppAlphabets;
