import App from './Numbers';
import AppAlphabets from './Alphabets';
import SideBar from './SideBar';
import Forms from './Forms';

import {useState} from 'react';

function Container() {
  const [showDiv, setShowDiv] = useState('dragdrop');


  const hideShow = (id) => {
    setShowDiv(id);
  }


  return (
    <div className="app">
      <SideBar hideShow={hideShow}/>
      <div className="container">
        {
        showDiv == 'dragdrop' ?
        <div id='dragdrop' style={{textAlign: 'center'}}> Drag Drop
          <App/>
          <AppAlphabets/>
        </div> : null
        }

        {
        showDiv == 'forms' ?
        <div id='forms'> Forms
            <Forms/>
        </div> : null
        }
      </div>      
    </div>
  );
}

export default Container;
