import { useSelector, useDispatch, useState } from "react-redux";
import Modal from "../components/Modal";

function PostList() {
  const list = useSelector((state) => state),
    dispatch = useDispatch(),
    [showModal, setShowModal] = useState(true);

  const click = (e) => {
    if (e.target.name == "delete") {
      dispatch({
        type: "DELETE_POST",
        id: e.target.id,
      });
    } else {
    }
  };

  return (
    <>
      <div className="section">
        <fieldset style={{ width: "100%" }}>
          <legend>Post List</legend>
          <table className="table" onClick={click}>
            <thead>
              <tr>
                <th>Title</th>
                <th>Message</th>
              </tr>
            </thead>
            {list.map((post, id) => {
              return (
                <tr key={post.id}>
                  <td>{post.title}</td>
                  <td>{post.message}</td>
                  <td>
                    <button id={post.id} name="edit" className="buttons">
                      edit
                    </button>
                  </td>
                  <td>
                    <button id={post.id} name="delete" className="buttons">
                      delete
                    </button>
                  </td>
                </tr>
              );
            })}
          </table>
        </fieldset>
        {showModal ? <Modal></Modal> : null}
      </div>
    </>
  );
}

export default PostList;
