import './App.css';
import App from './App';
import AppAlphabets from './AppAlphabets';
import SideBar from './SideBar';
import {useState} from 'react';

function Container() {
  const [showDiv, setShowDiv] = useState('dragdrop');


  const hideShow = (id) => {
    setShowDiv(id);
  }


  return (
    <div className="app">
      <SideBar hideShow={hideShow}/>
      <div className="container">
        {
        showDiv == 'dragdrop' ?
        <div id='dragdrop' style={{textAlign: 'center'}}> Drag Drop
          <App/>
          <AppAlphabets/>
        </div> : null
        }

        {
        showDiv == 'forms' ?
        <div id='forms'> Forms
            <form>
              <label>
                Name:
                <input type="text" name="name" />
              </label>
              <input type="submit" value="Submit" />
            </form>
        </div> : null
        }
      </div>

    </div>
  );
}

export default Container;
