import {useState} from 'react';

const api = 'http://apimeme.com/meme?meme=$1&top=$2&bottom=$3',
imageArray= ['Advice-Doge', 'Angry-Birds-Pig', 'Baby-Godfather', 'Close-Enough'],
apiUrl = {tt:'',bt:'',img:''};

function Api() {
const [request, setRequest] = useState(apiUrl);

const click = (e) => {
  setRequest({...request, img: e.target.id});
}

const blur = (e) => {

}

return (<>
    <h4>API Call</h4>
    <div className="section">

      <fieldset style={{width: '50%'}}>
        <legend>Create</legend>
        <form style={{paddingTop: '2%', textAlign: 'left'}}>
          <label htmlFor="toptxt">Top </label><br></br>
          <input type="text" id="toptxt" placeholder="Top text" onBlur={blur}></input>
          <br></br>
          <label htmlFor="bottomtxt">Bottom </label>
          <br></br>
          <input type="text" id="bottomtxt" placeholder="Bottom text" onBlur={blur}></input>
        </form>
      </fieldset>


      <fieldset style={{width: '50%'}} className='flexwrap' onClick={click}>
        <legend>Choose</legend>
        {imageArray.map((i,j) => {
          return(<div key={j} style={{margin:'4%', borderStyle: 'dotted', color: request.img == i ? 'red' : 'black'}}>
            <img id={i} src={`http://apimeme.com/thumbnail?name=${i}`}></img>
        </div>);
        })}
      </fieldset>
    </div>
    <button className='buttons' style={{width: '40%', marginTop: '2%', padding:'5px'}}>Generate</button>
  </>);
}

export default Api;
