import React, { useState } from "react";
const Counter = (props) => {
  const [count, setCount] = useState(0);

  const click = (e) => {
    if (props.error && count === 5) {
      throw Error("error");
    }

    if (e.target.id === "increment") {
      setCount(count + 1);
    } else {
      setCount(count - 1);
    }
  };

  return (
    <div style={{ width: "50%" }}>
      <fieldset>
        <legend>{props.name}</legend>
        <div>{count}</div>
        <button id="increment" onClick={click}>
          Increment
        </button>
        <button id="decrement" onClick={click}>
          Decrement
        </button>
      </fieldset>
    </div>
  );
};

export default Counter;
