import DragDrop from './DragDrop';
import SideBar from './SideBar';
import Forms from './Forms';
import Api from './Api';
import {useState} from 'react';

const completedArray = [
  {name:"one",bgcolor:"skyblue"}
],pendingArray = [
  {name:"Two",bgcolor: "yellow"},
  {name:"Three",bgcolor:"pink"}
],completedAlphaArray = [
  {name:"A",bgcolor:"skyblue"}
],pendingAlphaArray = [
  {name:"B",bgcolor: "yellow"},
  {name:"C",bgcolor:"pink"},
  {name:"D",bgcolor: "red"}
];

function Container() {
  const [showDiv, setShowDiv] = useState('dragdrop');

  const hideShow = (id) => {
    setShowDiv(id);
  }

  return (
    <div className="app">
      <SideBar hideShow={hideShow}/>
      <div className="container">
        {showDiv == 'dragdrop' ?
        <div id='dragdrop'> 
          <DragDrop completedArray={completedArray} pendingArray={pendingArray} horizontal={false}/>
          <hr></hr>
          <DragDrop completedArray={completedAlphaArray} pendingArray={pendingAlphaArray} horizontal={true}/>
        </div> : null}

        {showDiv == 'forms' ?
        <div id='forms'> 
           <Forms/>
        </div> : null}

        {showDiv == 'api' ?
        <div id='api'> 
           <h4>API</h4>
           <Api/>
        </div> : null}
      </div>      
    </div>
  );
}

export default Container;
