import { useSelector, useDispatch } from "react-redux";

function PostList() {
  const list = useSelector((state) => state),
    dispatch = useDispatch();

  return (
    <>
      <div className="section">
        <fieldset style={{ width: "100%" }}>
          <legend>Post List</legend>
          <table className="table">
            <thead>
              <tr>
                <th>Title</th>
                <th>Message</th>
              </tr>
            </thead>
            {list.map((post, id) => {
              debugger;
              return (
                <tr key={id}>
                  <td>{post.title}</td>
                  <td>{post.message}</td>
                  <td>
                    <button
                      className="buttons"
                      onClick={() => dispatch({ type: "EDIT_POST" })}
                    >
                      edit
                    </button>
                  </td>
                  <td>
                    <button className="buttons">delete</button>
                  </td>
                </tr>
              );
            })}
          </table>
        </fieldset>
      </div>
    </>
  );
}

export default PostList;
