import './App.css';
import {useEffect, useState} from 'react';

const completedArray = [
  {name:"Vue", category:"complete", bgcolor:"skyblue"}
];

const pendingArray = [
  {name:"Angular",category:"progress", bgcolor: "yellow"},
  {name:"React", category:"progress", bgcolor:"pink"}
];

function App() {
  const [completed, setCompletedTask] = useState(completedArray);
  const [progress, setProgressTask] = useState(pendingArray);

  const onDragStart = (ev, id) => {
    console.log('dragstart:',id);
    ev.dataTransfer.setData("id", id);
    }

    const onDragOver = (ev) => {
        ev.preventDefault();
    }

    const onDrop = (ev, cat) => {
      let id = ev.dataTransfer.getData("id");

    //   let tasks = progress.filter((task) => {
    //       if (task.name == id) {
    //           task.category = cat;
    //       }
    //       return task;
    //   });

    //  setTask
    }

  return (
    <div className="app">
        <div className="progress" onDragOver={(e)=>onDragOver(e)}
                    onDrop={(e)=>{onDrop(e, "wip")}}>
              first
              {
              progress.filter ((t) => {
                  <div key={t.name}
                      onDragStart = {(e) => onDragStart(e, t.name)}
                      draggable
                      className="draggable"
                      style = {{backgroundColor: t.bgcolor}}>
                      {t.name}
                  </div>
              })     
             }
        </div>

        <div className="complete" 
                    onDragOver={(e)=>onDragOver(e)}
                    onDrop={(e)=>onDrop(e, "complete")}> 
              second
              {
              completed.map ((t) =>
                  <div key={t.name}
                      onDragStart = {(e) => onDragStart(e, t.name)}
                      draggable
                      className="draggable"
                      style = {{backgroundColor: t.bgcolor}}>
                      {t.name}
                  </div>
              )     
             }
        </div>
    </div>
  );
}

export default App;
